body:
html, body {
	background: #fefefe;
	color: #444;
}
body {
	font-size: 112%;
	max-width: 33em;
	margin: 0 auto;
	width: 33em;
}
#reading-day-nav, #todays-readings, .top-link {
	display: none;
}
.devotion, .devotion div.esv-text, .devotion div.chunk.esv-text {
	font-family: "Hoefler Text", Constantia, "Lucida Bright", Lucidabright, "Lucida Serif", Lucida, "DejaVu Serif", "Bitstream Vera Serif", "Liberation Serif", Georgia, serif;
	line-height: 1.5em;
}
.excerpted-from, .footnotes, .noprint {
	border: none;
	font-size: 80%;
	margin: 0;
}
h2 {
	font-variant: small-caps;
	font-weight: normal;
	letter-spacing: 0.05em;
}
h3 {
	font-weight: bold;
	font-style: italic;
}
a {
	color: #e22;
}
p {
  margin-bottom: 0;
}
p + p {
  margin-top: 0;
}
p + p, .block-indent + p {
  text-indent: 2em;
}
p.line-group, .block-indent + p.same-paragraph {
  text-indent: 0;
  margin-top: 1em;
  margin-bottom: 0;
}
.block-indent {
	margin-left: 3em;
}
.footnote {
	font-size: 80%;
	font-style: italic;
	text-decoration: none !important;
}
.speaker {
	font-style: italic;
	margin-left: -3em;
}
.excerpted-from {
  margin-top: 3em;
  margin-bottom: 2em;
  text-indent: 0;
}
/* Optional */
form, .chapter-num, .verse-num, .noprint {
	display: none;
}
